from bs4 import BeautifulSoupfrom time import sleepimport requestsfrom selenium import webdriver# Initializing page to open in Firefox using Selenium's webdriverdef initialize():    URL = 'http://www.avaloncommunities.com/virginia/reston-herndon-apartments/avalon-reston-landing/floor-plans'    driver = webdriver.Firefox()    driver.get(URL)    sleep(5)    driver.find_element_by_xpath("/html/body/form/section[10]/div/div[1]/div/ul/li[3]/a").click()    url = "http://www.avaloncommunities.com/virginia/reston-herndon-apartments/avalon-reston-landing"    source_code = requests.get(url)    plain_txt = source_code.text    soup = BeautifulSoup(plain_txt, 'html.parser')    for table in soup.findAll('table'):        rows = table.findAll('tr')        for tr in rows:                cols = tr.findAll('td')                print(cols.get('href'))    sleep(10)    driver.quit()initialize()# while True:# avaloncrawler()       ## <table class="table"><thead><tr><th> </th><th>Apartment</th><th>Available</th><th>Starting At</th><th></th></tr></thead><tbody><tr><td> </td><td>030-0205</td><td>05/09/2016</td><td><span class="new-price">$2,115</span></td><td class="view-details"><a href="http://www.avaloncommunities.com/virginia/reston-herndon-apartments/avalon-reston-landing/apartment/VA565-VA565-030-0205">View Details</a></td></tr><tr><td> </td><td>010-0105</td><td>05/11/2016</td><td><span class="new-price">$2,127</span></td><td class="view-details"><a href="http://www.avaloncommunities.com/virginia/reston-herndon-apartments/avalon-reston-landing/apartment/VA565-VA565-010-0105">View Details</a></td></tr>  </tbody></table>